#-*- coding: utf-8 -*-import MySQLdbfor d in range(16):    db=MySQLdb.connect(        host="localhost",        user="root",        passwd="sa",        db="platform_test_%d" % d,        use_unicode=True,        charset="utf8")    c = db.cursor()    c.execute("""        CREATE TABLE `seq` (            `id` bigint(20) NOT NULL,            `tb` varchar(32) NOT NULL,            UNIQUE KEY `tb_UNIQUE` (`tb`)        ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='生成id序列号'        """)    c.close()    db.commit()    db.close()